{-# LANGUAGE OverloadedStrings #-}
{-# LANGUAGE TypeFamilies #-}
{-# LANGUAGE QuasiQuotes #-}
{-# LANGUAGE TemplateHaskell #-}
{-# LANGUAGE MultiParamTypeClasses #-}

import Yesod

data WebApp = WebApp

instance Yesod WebApp

mkYesod "WebApp" [parseRoutes|
  / HomeR GET
|]

getHomeR = defaultLayout [whamlet|
  <div>Hello, world!
|]

main = warpEnv WebApp

package main

import "fmt"

func main() {
    fmt.Println("hello world")
}

export const loader: LoaderFunction = async ({ params }) => {
  const db = await connect()

  const query = await db.models.Snippets.findOne({ _id: params.snippet })

  return query
}

def check_anagram(first_word, second_word):
  return sorted(first_word) == sorted(second_word)

print(check_anagram("silent", "listen"))   # True
print(check_anagram("ginger", "danger"))   # False

